sp_widget.client_script:
api.controller=function($rootScope,$scope) {
  /* widget controller */
  var c = this;
	c.selectItem= function(idx){
			var id=c.data.notes[idx].sys_id.display_value;
		  //var idobj= c.data.notes[idx].sys_id;
		//	console.log('Note IDOBJ: '+JSON.stringify(idobj));
		console.log('Note ID: ' + id);
    $rootScope.noteID = id;

		$rootScope.$emit('selected_note',id);

	}
};

sp_widget.script:
(function() {
  /* populate the 'data' object */
  /* e.g., data.table = $sp.getValue('table'); */
   data.notes=[];
	data.fields='number,title,sys_id,note';
	var noteGR = new GlideRecord('x_snc_createnotes_note');
//noteGR.addQuery('user', gs.getUser().getID());
noteGR.orderByDesc('sys_created_on');
noteGR.query();
while (noteGR.next()) {
	var obj={};
	$sp.getRecordElements(obj,noteGR,data.fields);
	data.notes.push(obj);
	console.log('Value of data is :'+JSON.stringify(obj));
}
})();
